mapper.select:
select * 
from scheduling
where doctor_id=#{doctorId} and date = #{date}

mapper.update:
update scheduling
set doctor_id = #{doctorId,jdbcType=INTEGER},
  date = #{date,jdbcType=VARCHAR},
  number = #{number,jdbcType=VARCHAR},
  state = #{state,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}
